import React, { Component, Suspense } from 'react';
import { useTranslation, withTranslation, Trans } from 'react-i18next';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  render() {
    const { t, i18n } = this.props;    
    return (
      <div className="App">
        <h2>{t('title')}</h2>
      </div>
    );
  }
}

export default App;

