import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import confirm from 'react-confirm';
//import config from '../config';

class UserRow extends Component {
    constructor(props) {
        super(props);
        this.handleDeleteRow = this.handleDeleteRow.bind(this);
    }

    handleDeleteRow (e) {
        e.preventDefault()
        if (!confirm ('Are you sure you want to delete this items?')){
            return false;
        }
        let url = 'http://localhost:8000/api/users/' + this.props.obj.id;
        axios.delete(url).then( 
            response => {
                this.props.deleteRow(this.props.index)
            }).catch( function (error){
                console.log(error);
            });
    }
    
    render() {
        return (
            // <tr>
            //     <td>
            //         { this.props.id}
            //     </td>
            //     <td>
            //         { this.props.name}
            //     </td>
            //     <td>
            //         { this.props.email} 
            //     </td>
            //     <td>
            //     <Link to={"/edit/" + this.props.id} className="btn btn-primary">Edit</Link>
            //     </td>
            //     <td>
            //         <button className='btn btn-danger' onClick={this.handleDelete}>Delete</button>
            //     </td>
            // </tr>
            <tr>
                <td>
                    {this.props.obj.id}
                </td>
                <td>
                    {this.props.obj.name}
                </td>
                <td>
                    {this.props.obj.email}
                </td>
                <td>
                    <Link className='btn btn-primary' to={'/users/edit/' + this.props.obj.id}>Edit</Link>
                </td>
                <td>
                    <button className='btn btn-danger' onClick={this.handleDelete}>Delete</button>
                </td>
            </tr>
        );
    }
}

export default UserRow;
