import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import UserRow from './UserRow';
import config from '../config';

const URL_SERVER = config.API_SERVER_URL;

class UserList extends Component {    
    constructor(props) {
        super(props);
        this.state = { users: [] };
        this.fetchRows = this.fetchRows.bind(this);
    }

    componentDidMount() {
        axios.get(URL_SERVER + 'users')
        .then(response => {
            this.setState({ users: response.data })
            // function (response) {
            console.log(response.data);
        })
        .catch(function (error) {
            console.log(error);
        });
    }

    fetchRows() {
        if(this.state.users instanceof Array){
            return this.state.users.map( (object,i) => {
                return (<UserRow />)
            } );
        }
    }

    render() {        
        return (
            <div className="container">
                <h1 className='titleMain'>Users List</h1>                                
                <div className='clearfix'>
                <Link className='btn btn-success pull-right' to='/users/create'>Add User</Link>
                </div><br />
                <table className='table table-hover'>
                <thead>
                    <tr>
                        <td>ID</td>
                        <td>Name</td>
                        <td>Email</td>
                        <td>Actions</td>
                    </tr>
                </thead>
                <tbody>     
                    {this.fetchRows()}                                  
                    {                  
                        this.state.users.map(function(user, i){
                            return (
                            <tr key={i}>
                                <td>{ user.id }</td>
                                <td>{ user.name }</td>
                                <td>{ user.email }</td>
                                <td>Edit / Delete</td>
                            </tr>
                                );
                        })
                    }
                </tbody>
                </table>             
            </div>
        );
    }
}

export default UserList;
