import React, { Component } from 'react';
import axios from 'axios';
import config from '../config';

class EditUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name:'',
            email:'',
            password:''
        };
        this.handleName = this.handleName.bind(this);
        this.handleEmail = this.handleEmail.bind(this);
        this.handlePassword = this.handlePassword.bind(this);
    }

    componentDidMount() {
        let url = config.API_SERVER_URL + 'users/' + this.props.match.params.id + '/edit';
        axios.get(url).then(
            response => {
                this.setState(response.data);
                console.log(response.data);
            }
        ).catch(function(error){
            console.log(error);
        });
    }

    handleName(){

    }

    handleEmail(){

    }
    
    handlePassword(){

    }

    render() {        
        return (
            <div className="container">
                <h2>Page Edit User</h2>        
                <form>
                    <div className='form-group'>
                        <label htmlFor='name'>Name</label>
                        <input type='text' className='form-control' id='name' placeholder='Name'
                        value={this.state.name} onChange='' required />
                    </div>
                    <div className='form-group'>
                        <label htmlFor='email'>Email</label>
                        <input type='text' className='form-control' id='email' placeholder='Email'
                        value={this.state.email} onChange='' required />
                    </div>
                    <div className='form-group'>
                        <label htmlFor='password'>Password</label>
                        <input type='password' className='form-control' id='password' placeholder='Password'
                        value={this.state.password} onChange='' required />
                    </div>
                    <button type='submit' className='btn btn-primary'>Update User</button>                    
                </form>
            </div>
        );
    }
}

export default EditUser;
