import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import App from '../App';
import UserRow from './UserRow';
//import config from '../config';

class UserList extends Component {
    constructor(props) {
        super(props)
        this.state = {users: []}
    }

    componentDidMount (){
        axios.get('http://localhost:8000/api/users').then(
            response => {
                this.setState({users: response.data})
            }
        ).catch(function (error){
            console.log(error);            
        })
    }

    deleteRow (key){
        var users = [...this.state.users];
        users.splice(key, 1);
        this.setState({users});
    }

    fetchRows (){
        if (this.state.users instanceof Array){
            return this.state.users.map( (object, i) => {
                return <UserRow obj={object} key={i} index={i} deleteRow={this.deleteRow.bind(this)} />
            } )
        }
    }
    
    render() {
        return (
            <App>
                <h1>Users</h1>
                <div className='clearfix'>
                <Link className='btn btn-success pull-right' to='/users/create'>Add User</Link>
                </div><br />
                <table className='table table-hover'>
                <thead>
                    <tr>
                    <td>ID</td>
                    <td>Name</td>
                    <td>Email</td>
                    <td>Actions</td>
                    </tr>
                </thead>
                <tbody>
                    {this.fetchRows.bind(this)}
                </tbody>
                </table>                
            </App>
        );
    }
}

export default UserList;
