        })
        return (
            <App>
                <h1>Users</h1>
                <div className='clearfix'>
                <Link className='btn btn-success pull-right' to='/users/create'>Add User</Link>
                </div><br />
                <table className='table table-hover'>
                <thead>
                    <tr>
                    <td>ID</td>
                    <td>Name</td>
                    <td>Email</td>
                    <td>Actions</td>
                    </tr>
                </thead>
                <tbody>
                    {/* {this.fetchRows.bind(this)} */}
                    {users}
                </tbody>
                </table>                
            </App>
        );
    }
}

export default UserList;
